#大小写转换str7 = "bmw"print(str7.upper())#转换为大写print(str7.lower())#转换为小写print("how are you".capitalize())#将首字母转换为大写print("michael jackson sam".title())#单词首字母转换为大写print("abcEFG".swapcase())#大小写交换#制表符与换行符table ="姓名\t\t性别\t\t年龄\n赵四\t\t男生\t\t42"print(table)#删除空白space_str = "    hello world    "#len（）获取字符串长度lstr = len(space_str)print(lstr)nospace_str = space_str.strip()print(nospace_str)print(len(nospace_str))#字符串查找source = "Nice to meet you, i need your help!"index = source.find("ee")print(index)#检查是否有ee字样存在语句isexist = "ee" in sourceprint(isexist)#字符串替换str8 = "this is string example...wow!!! this is really string"pstr8 = str8.replace("is","was")print(pstr8)pstr8 = str8.replace("is","was",3)print(pstr8)